import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2020");
  const filterChangeHandler = (year) => {
    setFilteredYear(year);
  };
  const filtredExpenses=props.expenses.filter(expense=>expense.date.getFullYear().toString()===filteredYear);
  let expenseContent=<p style={{color:"red", textAlign: "center"}}>No Expenses Found</p>;
  if(filtredExpenses.length>0){
    expenseContent= filtredExpenses.map((expense) => (
      <ExpenseItem
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
        key={expense.id}
      />
    ))

  }
  return (
    <Card className="expenses">
      <ExpensesFilter
        selected={filteredYear}
        onFilterChange={filterChangeHandler}
      />
       {expenseContent}
    </Card>
  );
}
export default Expenses;
