import Expenses from "../src/components/Expenses/Expenses";
import React,{useState} from "react";
import '../src/components/NewExpense/NewExpense';
import NewExpense from "../src/components/NewExpense/NewExpense";

const DUMMY_EXPENSES = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];
function App() {
  const [expenses,setExpenses]=useState(DUMMY_EXPENSES);
  const addExpenseHandler = expense => {
    setExpenses((prevExpense)=>{
      return[expense,...prevExpense]
    })
  };
  return (
    // React.createElement('div',{},React.createElement('h2',{},'Let\'s get started!'),React.createElement(Expenses,{expenses:expenses}))
    <div>
     <NewExpense onAddExpense={addExpenseHandler} />
     <Expenses expenses={expenses}></Expenses>
    </div>
  );
}

export default App;
