import React,{useState} from 'react';
import './ExpenseForm.css';
function ExpenseForm(){
    // const [title,setTitle]=useState('');
    // const [amount,setAmount]=useState('');
    // const [date,setDate]=useState('');
    const [userInput,setUserInput]=useState({
        title:'',
        amount:'',
        date:''
    })
    const tileChangeHandler=(event)=>{
        setUserInput({
            ...userInput,
            title:event.target.value
        })
    }
    const amountChangeHandler=(event)=>{
        setUserInput({
            ...userInput,
            amount:event.target.value
        })
    }
    const dateChangeHandler=(event)=>{
        setUserInput({
            ...userInput,
            date:event.target.value
        })
    }
    return(
        <div>
            <form>
                <div className="new-expense__controls">
                    <div className="new-expense__control">
                    <label>Title</label>
                    <input type='text' onChange={tileChangeHandler}/>
                    </div>
                    <div className="new-expense__control">
                    <label>Amount</label>
                    <input type='number' min='1' step='1' onChange={amountChangeHandler}/>
                    </div>
                    <div className="new-expense__control">
                    <label>Date</label>
                    <input type='date' min='1-1-2019' max='31-12-2022' onChange={dateChangeHandler}/>
                    </div>
                    <div className="new-expense__actions">
                        <button type='submit'> Add Expense</button>
                    </div>
                </div>
            </form>
        </div>
    )
}

export default ExpenseForm;
